import React from "react";
import data from "./data.json";

const ShowItem = () => {
  return (
    <div>
      {data.map((item, id) => {
        return (
          <div className={"row"}>
            <div className="col-6">
              <img src={require(`${item.url}`)} width={"100%"} />
            </div>
            <div className="col-6">
              <h1>{item.name}</h1>

              <p>Price: {item.price}$</p>

              <p>{item.description}</p>

              <br />
              <br />

              <input type="number" />

              <br />
              <br />

              <button className="btn btn-primary">Add to Cart</button>
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default ShowItem;
